listeners.loadcontent:
function(dialog){
var dialogRef = dialog
                            if(dialogRef && dialogRef.path){
                                var dlgPath = dialogRef.path.replace("/jcr:content","");
                                var url = CQ.HTTP.noCaching(dlgPath + ".cloudservices.json");
                                var response = CQ.HTTP.get(url);
                                if(response){
                                    var inheritData = CQ.HTTP.eval(response); 
                                    if(inheritData){
                                        var configs = inheritData["cq:cloudserviceconfigs"];
                                        if(configs){
                                            var configURL = "";
											var defaultPath = "/etc/cloudservices/mailchimp";
                                            if($.isArray(configs)){

                                                $.each(configs, function( index, value ) {
                                                    if(value.indexOf(defaultPath) > -1){

                                                        configURL = value;
                                                    }
                                                });
                                            }else{
												if(configs.indexOf(defaultPath) > -1){
													configURL = configs;
                                                }
                                            }
                                            if(configURL.length > 0){
												var listSelect = dialogRef.findById("list-selection");
												$.ajax({
                                                    url: '/services/mailchimp/import/lists?source=cloud&path=' + configURL,
                                                    type: 'GET',
                                                    success: function(data){
                                                        if(data){
                                                            var jsonData = JSON.parse(data);
                                                            listSelect.reset();
                                                            listSelect.setOptions(jsonData);
															var listID = inheritData["default-list"];
															if( listID && listID.length > 0){
																listSelect.setValue(listID);
															}
															listSelect.doLayout(false,false);
														}
                                                    },
                                                    error:function(data){
                                                        console.log(data);
                                                    }
                                                });
                                            }
                                        }
                                    }

                                }

                            }
}
